import React, { Component } from "react";
import App from "./App";
import Home from "./Home";
import Contact from "./Contact";
import Header from "./Header";
import About from "./About";

export default class Questionnaire extends Component {

    
    constructor(props) {
        super(props)

        this.state= {
            userAnswer: null,
            currentIndex: 0,
            answerChoices: [],
            quizEnd: false,
            score: 0,
            disabled: true
        }
    }

    startQuiz = () => {
        const {currentIndex} = this.state;
        this.setState(() => {
            return {
                question: QuestionData[currentIndex].question,
                answerChoices: QuestionData[currentIndex].answerChoices,
                answer: QuestionData[currentIndex].answer
            }
        })
    }
    nextQuestionHandler = () => {
        const {userAnswer, answer, score} = this.state

        if (userAnswer === answer) {
            this.setState ({
                score: score + 1
            })
        }
        this.setState({
            currentIndex: this.state.currentIndex + 1,
            //userAnswer: null
        })
    }

    componentDidMount() {
        this.startQuiz();
    }
    
    checkAnswer = answer => {
        this.setState({
            userAnswer: answer,
            disabled: false
        })
    }

    componentDidUpdate(prevProps, prevState) {
        const{currentIndex} = this.state;
        if (this.state.currentIndex !== prevState.currentIndex) {
            this.setState(() => {
                return {
                    question: QuestionData[currentIndex].question,
                    answerChoices: QuestionData[currentIndex].answerChoices,
                    answer: QuestionData[currentIndex].answer
                }
            })
        }
    }
    finishHandler = () => {
        if (this.state.currentIndex === QuestionData.length - 1) {
            this.setState({
                quizEnd: true
            })
        }
    }



    render() {

        return (
            <h1> Questionnaire Screen </h1>
        )
    }
}
